mapper.update:
update member
set status
= '1' where mem_no=#{mem_no}

update member set
password = #{password},
mem_id =
#{mem_id},
phone = #{phone}
where mail = #{mail}

mapper.select:
select * from ex_book
where
mem_no=#{mem_no} and ex_no=#{ex_no}

select * from equip_list
where
mem_no = #{mem_no} and equip_id = #{equip_id}
